int createTempVarAndReturnOffset(hash_table_holder root, char type)
{
    char temp[21];
    sprintf(temp, "t%d", temp_counter);
    node_content content;
    if (type == 'i')
    {
        content.type = INTEGER;
        insertIntoHashTable(root->hash_table, temp, GLOBAL_COUNTER, PRIMITIVE_TYPE, content);
        GLOBAL_COUNTER += 4;
    }
    else if (type == 'r')
    {
        content.type = REAL;
        insertIntoHashTable(root->hash_table, temp, GLOBAL_COUNTER, PRIMITIVE_TYPE, content);
        GLOBAL_COUNTER += 16;
    }
    else if (type == 'b')
    {
        content.type = BOOLEAN;
        insertIntoHashTable(root->hash_table, temp, GLOBAL_COUNTER, PRIMITIVE_TYPE, content);
        GLOBAL_COUNTER += 1;
    }
}

